import "./SelectTickets.css";
import BackArrow from "../../../assets/back-arrow.svg";
import ContinueArrow from "../../../assets/continue-arrow.svg";
import WasteBasket from "../../../assets/waste-basket.svg";
import Button from "../../Button/Button";
import {useState,useEffect} from 'react';

const Tickets = (props) => {

    useEffect(()=>{
        console.log("ops, changed: ",props.tickets)
    },[props.tickets])

    return props.tickets.map((ticket)=>{
        return <div key={ticket.key} className="ticket">
            <div>
                <span>Age:</span>
                <input type="number" className="ticket-age-input" min="1"/>
            </div>

            <div>
                <span>Name:</span>
                <input type="text"/>
            </div>

            <div>
                <span>Type:</span>
                <select type="text" defaultValue={"DEFAULT"}>
                    <option value="DEFAULT" disabled style={{color: "black", fontWeight: "bold"}}>Select ticket</option>
                    <option value="1">Standard</option>
                    <option value="2">Child (&lt;14 y/o)</option>
                    <option value="3">Student</option>
                    <option value="4">Pensionary</option>
                </select>
            </div>

            <img src={WasteBasket} alt="waste-basket" onClick={()=>{
                console.log(21)}}/>
        </div>
    })
}

function SelectTickets(props) {
    const [ticketList,changeTicketList] = useState([])


    const deleteTicket = (id) =>{
        changeTicketList(ticketList.filter(ticket=>ticket.id!==id))
    }

    // const changeTicketDataHandler = (ticketId,field) =>{
    //
    // }

    const addTicketHandler = () => {
        let tempList = [...ticketList]
        let key = "T-" + new Date().getTime()

        tempList.push({
            id: key,
            age: null,
            name:null,
            type:null
        })

        changeTicketList(tempList)
    }

    return (
        <div className="book-page-container">
            <h2>Title</h2>

            <h3>Book your tickets</h3>

            <div className="price-list">
                <p>Price list</p>
                <p>Standard ......... 3e</p>
                <p>Child (&lt; 14 years old) ......... 3e</p>
                <p>Student ......... 5e</p>
                <p>Pensionary ......... 3e</p>
            </div>

            <div>
                <input type="checkbox"/>
                <label>Check this box if there is one ticket for the person who is owning this account</label>
            </div>

            <div className="tickets-container">
                {  ticketList.map((ticket)=>{
                    return(<div key={ticket.id} className="ticket">
                        <div>
                            <span>Age:</span>
                            <input type="number" className="ticket-age-input" min="1"/>
                        </div>

                        <div>
                            <span>Name:</span>
                            <input type="text" placeholder="Optional"/>
                        </div>

                        <div>
                            <span>Type:</span>
                            <select type="text" defaultValue={"DEFAULT"}>
                                <option value="DEFAULT" disabled style={{color: "black", fontWeight: "bold"}}>Select ticket</option>
                                <option value="1">Standard</option>
                                <option value="2">Child (&lt;14 y/o)</option>
                                <option value="3">Student</option>
                                <option value="4">Pensionary</option>
                            </select>
                        </div>

                        <img src={WasteBasket} alt="waste-basket" onClick={()=>{deleteTicket(ticket.id)}}/>
                    </div>)
                })
                }

                <Button text="Add new ticket" event={()=>{addTicketHandler()}}/>
            </div>



            <div className="buttons-container">
                <img src={BackArrow} alt="back-arrow"/>
                <img src={ContinueArrow} alt="continue-arrow" onClick={props.forward}/>
            </div>
        </div>
    );
}

export default SelectTickets;
