import "./SelectDay.css";
import Button from "../../Button/Button";
import {useEffect, useState} from 'react';
import HeaderText from "../../HeaderText/HeaderText";
import LoadingAnimation from "../../LoadingAnimation/LoadingAnimation";
import {isDateInPast} from "../../../utils/utils";

const Results = ({value})=>{
    const [data, setData] = useState({});

    const loadData = async () =>{
        // const response = await fetch(`http://localhost:3000/?type=get-movies-by-date&date=${value}`)

        const response = await fetch(`http://localhost:3000/get-movies-by-date?date=${value}`)
        response.json()
            .then(res=>{
                console.log("Response: ",res)
                setData(res)
            })
    }

    useEffect(()=>{
        setData({})
        loadData();
        return ()=>{};
    },[value])


    // if(isDateInPast(value))
    //     return <p>date is in past</p>

    if(data.content)
        if(!data.error){
            console.log(data.error)
            return <>
                {data.content.map(movie=><div className="selectDay-movie">
                    <img alt="example-img"/>

                    <div className="selectDay-movie-info">
                        <p>{movie.name} (2020)</p>
                        <Button text="Book now"/>
                        <p>Drama<br/>1h 80m</p>
                    </div>

                    <div className="selectDay-movie-hours">
                        <ul>
                            <li>13:40</li>
                            <li>14:40</li>
                            <li>15:40</li>
                            <li>17:40</li>
                        </ul>
                    </div>
                </div>)}

            </>
        }
        else
            return <p>no results..</p>
    else
        return <LoadingAnimation/>


}


function SelectDay() {
    const [selectedDay,changeSelectedDay] = useState('')
    return (
        <div className="selectDay section">
            <HeaderText text="Select a day"/>
            <input type="date" onChange={(ev)=>{changeSelectedDay(ev.target.value)}}/>
            {
                selectedDay !== '' &&
                <div className="selectDay-results">
                    <h2>Scheduled movies for {selectedDay.split('-')[2]}/{selectedDay.split('-')[1]}</h2>
                    <div className="selectDay-list-of-results">

                        <Results value={selectedDay}/>

                        {/*<div className="selectDay-movie">*/}
                        {/*    <img src={ExampleImage} alt="example-img"/>*/}

                        {/*    <div className="selectDay-movie-info">*/}
                        {/*        <p>Lovehard (2020)</p>*/}
                        {/*        <Button text="Book now"/>*/}
                        {/*        <p>Drama<br/>1h 80m</p>*/}
                        {/*    </div>*/}

                        {/*    <div className="selectDay-movie-hours">*/}
                        {/*        <ul>*/}
                        {/*            <li>13:40</li>*/}
                        {/*            <li>14:40</li>*/}
                        {/*            <li>15:40</li>*/}
                        {/*            <li>17:40</li>*/}
                        {/*        </ul>*/}
                        {/*    </div>*/}
                        {/*</div>*/}

                    </div>
                </div>
            }
        </div>
    );
}

export default SelectDay;
