const SearchMovieResults = ({value})=>{
    const [data, setData] = useState({});
    const loadData = async () =>{
        if(!value) return;
        value = value.replaceAll(" ","%20")
        const response = await fetch(`http://localhost:3000/get-movies?title=${value}`)
        response.json()
            .then(res=>{
                console.log(res)
                setData(res)
            })
    }


    useEffect(()=>{
        if(value === "")
            return;


        setData({})
        loadData();
        return ()=>{};
    },[value])



    if(value === "")
        return ""


    if(data.code==21)
