import "./TicketInfo.css";
import {useState} from  'react';

function TicketInfo(props) {
    const [activeForm,setActiveForm] = useState("")

    const changeUsernameButtonHandler = () =>{
        console.log("changing user")
        setActiveForm("user")
    }

    const changeEmailButtonHandler = () =>{
        console.log("changing email")
        setActiveForm("email")
    }

    const changePasswordButtonHandler = () =>{
        console.log("changing pass")
        setActiveForm("pass")
    }

    return (
        <div className="account-section ticket-section">
            <div className="account-buttons">
                <button onClick={changeUsernameButtonHandler}>Change your username</button>
                <button onClick={changeEmailButtonHandler}>Change your email adress</button>
                <button onClick={changePasswordButtonHandler}>Change your password</button>
            </div>

            {
                activeForm=="user" && <p>user</p>
            }

            {
                activeForm=="email" && <p>email</p>
            }

            {
                activeForm=="pass" && <p>pass</p>
            }

        </div>
    );
}

export default TicketInfo;
