import "./SearchMovie.css";
import ExampleImage from "../../../assets/example-movie-image.jpg";

import Button from "../../Button/Button";
import LoadingAnimation from "../../LoadingAnimation/LoadingAnimation";
import {useEffect,useState} from 'react';


const Value = ({value})=>{
    const [data, setData] = useState({});
    const loadData = async () =>{

        const res = await fetch(`https://api.agify.io/?name=${value}`)

        setData(await res.json());
    }

    useEffect(()=>{
        setData({})
        loadData();
        return ()=>{};
    },[value])

    if(data.name)
        return <h1>{data.name}, {data.age}</h1>
    else
        return <h1>loading</h1>


}
var Timeout;
function SearchMovie() {
    const [movieValue,changeMovieValue] = useState("")

    const searchValueHandler = (e) =>{
        clearTimeout(Timeout)
        Timeout = setTimeout(()=>{
            setPageNumber(1)
            changeMovieValue(e.target.value)
        },300)

    }

    return (
        <div className="searchMovie section">
            <h1>Search a movie</h1>
            <input
                placeholder="Type here the movie title"
                onChange={e=>changeMovieValue(e.target.value)}
                type="text"/>

            <Value value={movieValue}/>

            {
                movieValue !== "" &&
                <div className="selectDay-results">
                    <h2>Results for {movieValue}</h2>
                    <div className="selectDay-list-of-results">
                        <div className="selectDay-movie">
                            <img src={ExampleImage}/>

                            <div className="selectDay-movie-info">
                                <p>Lovehard (2020)</p>
                                <p>Drama<br/>1h 80m</p>
                            </div>

                            <div className="searchMovie-willbeplayed">
                                <p>Will be played in:</p>
                                <ul>
                                    <li>3 june</li>
                                    <li>17 june</li>
                                    <li>14 august</li>
                                    <li>18 august</li>
                                </ul>
                            </div>
                        </div>

                        <div className="selectDay-movie">
                            <img src={ExampleImage}/>

                            <div className="selectDay-movie-info">
                                <p>Lovehard (2020)</p>
                                <p>Drama<br/>1h 80m</p>
                            </div>

                            <div className="searchMovie-willbeplayed">
                                <p>Not scheduled soon.</p>
                                <Button text="Suggest it!"/>
                            </div>
                        </div>

                    </div>
                </div>
            }



        </div>
    );
}

export default SearchMovie;
