import "./Navbar.css";
import navbarLogo from "../.././assets/navbar-logo.png"
import {Link} from "react-router-dom";
import {useEffect,useState} from 'react'

function Navbar() {

    const [links,setLinks] = useState([])
    useEffect(()=>{
        const path = window.location.pathname
        let linkArray = []
        if(path !== "/")
            linkArray.push(<Link to="/">Home</Link>)

        if(path !== "/login" && path !== "/signup"){
            linkArray.push(<Link to="/login">Login</Link>)
            linkArray.push(<Link to="/signup">Sign up</Link>)
        }

        linkArray.push(<Link to="/404">Account</Link>)
        linkArray.push(<button>Sign out</button>)

        setLinks(linkArray)
    })

    return (
        <div className="navbar">
            <img src={navbarLogo}/>
            <div className="navbar-links">
                {links}
            </div>
        </div>
    );
}

export default Navbar;
