                    {
                        (data.error || !data.content[1]) &&
                        <>
                            <input placeholder="Type the movie's ID" type="number" onChange={e=>props.setFirstId(e.target.value)}/>
                            <Button text={"Set movie"} event={()=>{setMovie(1)}}/>
