import "./Navbar.css";
import navbarLogo from "../.././assets/navbar-logo.png"
import {Link, useLocation, useParams} from "react-router-dom";
import {useEffect,useState} from 'react'

function Navbar() {

    const [links,setLinks] = useState([])

    const location = useLocation()

    useEffect(()=>{
        // <Link to="/" key="home-link">Home</Link>
        console.log("location: ",location)
        switch(location){
            case "/":
                setLinks([
                    <Link to="/login" key="login-link">Login</Link>,
                    <Link to="/signup" key="signup-link">Sign up</Link>,
                    <Link to="/404" key="account-link">Account</Link>,
                    <button key="signout-link">Sign out</button>
                ])
                break;
            case "/login":
                setLinks([<Link to="/" key="home-link">Home</Link>])
                break;
            case "/signup":
                setLinks([<Link to="/" key="home-link">Home</Link>])
                break;
            case "/404":
                setLinks([<Link to="/" key="home-link">Home</Link>])
                break;
            case "/panel":
                setLinks([<Link to="/" key="home-link">Home</Link>])
                break;
            default:
                setLinks([<Link to="/" key="home-link">Unknown location (see Navbar.js)</Link>])
        }

        const path = window.location.pathname
        let linkArray = []
        if(path !== "/")
            linkArray.push(<Link to="/" key="home-link">Home</Link>)

        if(path !== "/login" && path !== "/signup"){
            linkArray.push(<Link to="/login" key="login-link">Login</Link>)
            linkArray.push(<Link to="/signup" key="signup-link">Sign up</Link>)
        }

        linkArray.push(<Link to="/panel" key="panel-link">Panel</Link>)
        linkArray.push(<Link to="/404" key="account-link">Account</Link>)
        linkArray.push(<button key="signout-link">Sign out</button>)

        setLinks(linkArray)
    },[])

    return (
        <div className="navbar">
            <img src={navbarLogo} alt="remigoo logo"/>
            <div className="navbar-links">
                {links}
            </div>
        </div>
    );
}

export default Navbar;
