import "./Trending.css";
import "../MainPage.css"
import Button from "../../Button/Button";

import ExampleImage from '../../../assets/example-movie-image.jpg'
import HeaderText from "../../HeaderText/HeaderText";
import {beautifyMinutes} from "../../../utils/utils";
import LoadingAnimation from "../../LoadingAnimation/LoadingAnimation";

const Results = ()=>{
    const [data, setData] = useState({});

    const loadData = async () =>{
        const response = await fetch(`http://localhost:3000/getTrending`)
        response.json()
            .then(res=>{
                console.log("Response: ",res)
                setData(res)
            })
    }

    useEffect(()=>{
        setData({})
        loadData();
        return ()=>{};
    })


    // if(isDateInPast(value))
    //     return <p>date is in past</p>

    if(data.content)
        if(!data.error){
            return <>
                {data.content.map(movie=><div className="selectDay-movie" key={"movie-by-date-"+movie.title}>
                    <img src={movie.img} alt={`img-of-${movie.title}`}/>

                    <div className="selectDay-movie-info">
                        <p>{movie.title} ({movie.year})</p>
                        <Button text="Book now"/>
                        <p>{movie.genre}<br/>{beautifyMinutes(movie.duration)}</p>
                    </div>

                    <div className="selectDay-movie-hours">
                        <ul>
                            <li>13:40</li>
                            <li>14:40</li>
                            <li>15:40</li>
                            <li>17:40</li>
                        </ul>
                    </div>
                </div>)}

            </>
        }
        else
            return <p>There is no trending.</p>
    else
        return <LoadingAnimation/>


}

function Trending() {
    return (
        <div className="trending section">
            <HeaderText text="Trending"/>
            <div className="trending-boxes">
                <div className="trending-box">
                    <img src={ExampleImage} alt="example-img"/>
                    <Button text="Book a ticket"/>
                </div>

                <div className="trending-box">
                    <img src={ExampleImage} alt="example-img"/>
                    <Button text="Book a ticket"/>
                </div>

                <div className="trending-box">
                    <img src={ExampleImage} alt="example-img"/>
                    <Button text="Book a ticket"/>
                </div>
            </div>
        </div>

    );
}

export default Trending;
