import "./Navbar.css";
import navbarLogo from "../.././assets/navbar-logo.png"
import {Link, useLocation, useParams} from "react-router-dom";
import {useEffect,useState} from 'react'

function Navbar() {
    const [links,setLinks] = useState([])
    const location = useLocation()

    useEffect(()=>{
        let Links = []

        switch(location.pathname){
            case "/":
                Links.push(<Link to="/login" key="login-link">Login</Link>)
                Links.push(<Link to="/signup" key="signup-link">Sign up</Link>)
                Links.push(<Link to="/404" key="account-link">Account</Link>)
                Links.push(<button key="signout-link">Sign out</button>)
                Links.push(<Link to="/panel" key="panel-link">Panel</Link>)
                break;
            case "/login":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "/signup":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "/404":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "/book":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "/panel":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                Links.push(<Link to="/404" key="account-link">Account</Link>)
                Links.push(<button key="signout-link">Sign out</button>)
                break;
            default:
                Links.push(<Link to="/" key="home-link">Unknown location (see Navbar.js)</Link>)
                break;
        }
        setLinks(Links)
    },[useLocation()])

    return (
        <div className="navbar">
            <img src={navbarLogo} alt="remigoo logo"/>
            <div className="navbar-links">
                {links}
            </div>
        </div>
    );
}

export default Navbar;
