import "./TicketInfo.css";
import {useState} from  'react';
import Button from "../../Button/Button";

const UserForm = () =>{
    return(<>
        <form className="account-form">
            <label>Old username: Mihai</label>
            <label>New username:</label>
            <input type="text"/>
            <Button text="Change username"/>
        </form>
        </>)
}

const EmailForm = () =>{
    return(<>
        <form className="account-form">
            <label>Old email: Mihai</label>
            <label>New email:</label>
            <input type="text"/>
            <Button text="Change email"/>
        </form>
    </>)
}

const PasswordForm = () =>{
    return(<>
        <form className="account-form">
            <label>Old password:</label>
            <input type="text"/>
            <label>New password:</label>
            <input type="text"/>
            <Button text="Change password"/>
        </form>
    </>)
}

function TicketInfo(props) {
    const [activeForm,setActiveForm] = useState("email")

    return (
        <div className="account-section ticket-section">
            <div className="account-buttons">
                <button onClick={()=>{setActiveForm("user")}}>Change your username</button>
                <button onClick={()=>{setActiveForm("email")}}>Change your email adress</button>
                <button onClick={()=>{setActiveForm("pass")}}>Change your password</button>
            </div>

            {
                activeForm=="user" && <UserForm/>
            }

            {
                activeForm=="email" && <EmailForm/>
            }

            {
                activeForm=="pass" && <PasswordForm/>
            }

        </div>
    );
}

export default TicketInfo;
