import "./DateTime.css";
import BackArrow from '../../../assets/back-arrow.svg'
import ContinueArrow from '../../../assets/continue-arrow.svg'
import {useEffect} from 'react';
import {beautifyMinutes} from "../../../utils/utils";

function DateTime(props) {

    const changeDayHandler = (value) =>{
        props.changeSelectedDay(value)
        props.changeSelectedHour("")
    }

    useEffect(()=>{
        let mySchedule = {}
        if(props.DBMovie){

            props.DBMovie.schedule.forEach(schedule=>{
                if(mySchedule[schedule.date])
                    mySchedule[schedule.date].push({time:schedule.time,scheduleId:schedule.id})
                else
                    mySchedule[schedule.date] = [{time:schedule.time,scheduleId:schedule.id}]
            })
        }

        console.log(mySchedule)
    },[props.DBMovie])

    return (
        <div className="book-page-container">

            {
                props.DBMovie && <h2>{props.DBMovie.movie.title}</h2>
            }

            {
                props.DBMovie &&
                <iframe  src={`https://www.youtube.com/embed/${props.DBMovie.movie.trailer}`}
                    title="YouTube video player" frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen></iframe>
            }


            {
                props.DBMovie &&  <p className="genre-time">{props.DBMovie.movie.genre}<br/>{beautifyMinutes(props.DBMovie.movie.duration)}</p>
            }

            <div className="pickers-container">
                <div className="date-picker">
                    <h3>Select a day</h3>
                    <div className="date-results">
                        {
                            props.DBMovie &&
                                <>
                                    <p>heya</p>
                                    {/*{props.DBMovie.schedule.map(schedule=><p className={"24 june 2021" === props.selectedDay ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{changeDayHandler("24 june 2021")}}>24 june 2021</p>)}*/}
                                </>
                        }
                        <p className={"24 june 2021" === props.selectedDay ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{changeDayHandler("24 june 2021")}}>24 june 2021</p>
                        <p className={"24 august 2021" === props.selectedDay ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{changeDayHandler("24 august 2021")}}>24 august 2021</p>
                        <p className={"24 december 2021" === props.selectedDay ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{changeDayHandler("24 december 2021")}}>24 december 2021</p>
                    </div>
                </div>
                <div className={props.selectedDay !== "" ? "hour-picker" : "hour-picker invisible-book-btn"}>
                    <h3>Select an hour</h3>
                    <div className="hour-results">
                        <p className={"23:40" === props.selectedHour ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{props.changeSelectedHour("23:40")}}>23:40</p>
                        <p className={"23:41" === props.selectedHour ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{props.changeSelectedHour("23:41")}}>23:41</p>
                        <p className={"23:42" === props.selectedHour ? "picker-result active-picked-result" : "picker-result"} onClick={()=>{props.changeSelectedHour("23:42")}}>23:42</p>
                    </div>
                </div>
            </div>

            <div className="buttons-container">
                <img src={BackArrow} alt="back-arrow" className="invisible-book-btn"/>
                <img src={ContinueArrow} alt="continue-arrow" className={(props.selectedHour!=="" && props.selectedDay!=="") ? "" : "disabled-book-btn"} onClick={props.forward}/>
            </div>
        </div>
    );
}

export default DateTime;
