import "./LoginSignup.css";
import Button from "../Button/Button";
import {useState} from 'react';
import {Link} from "react-router-dom";
import {popUpError} from '../../utils/utils.js'
import {useAccountData, useAccountDataUpdate} from "../AccountContext/AccountProvider";

function LoginPage() {
    const [error,setError] = useState("")
    const [inputEmail,changeInputEmail] = useState("")
    const [inputPassword,changeInputPassword] = useState("")

    const AccountDataUpdate = useAccountDataUpdate()
    const AccountData = useAccountData()

    async function LoginRequest(obj){
    //    email,password
        const response = await fetch(`http://localhost:3000/?type=login&email_address=${obj.email}&password=${obj.password}`)
        return await response.json()
    }


    const handleForm = async (e)=>{
        e.preventDefault();

        // AccountDataUpdate({
        //                     isLoggedIn: true,
        //                         userData:{
        //                         userId: "someuserid",
        //                         userName: "somename",
        //                         userEmail: "someemail",
        //                         token: 2323
        //                         }
        //                  })

        //complete all fields
        if(inputEmail === '' || inputPassword === '' ){
            setError("Complete all fields.")
            popUpError()
            return;
        }

        console.log('passed')
        const response = await LoginRequest({
            email:inputEmail,
            password:inputPassword
        })

        if(response.error){
            setError(response.content)
            popUpError()
        }

        console.log(response)
    }

    /*
    *           LOGIN...
    *
    * LoginStatus={
                   isLoggedIn: <true/false>
                   userData:{
                           userId: <id>
                           userName: <name>
                           userEmail: <email>
                           token: <SERVER_TOKEN>
                            }
                 }
    * */



    return (
        <div className="login-signup-form-container">

            <h2>Login</h2>
            <form>
                <label>Email address</label>
                <input type="text" placeholder="email" required onChange={(e)=>{changeInputEmail(e.target.value)}}/>

                <label>Password</label>
                <input type="password" placeholder="password" required onChange={(e)=>{changeInputPassword(e.target.value)}}/>

                <p id="account-error">{error}</p>

                <Button text="Login" event={handleForm}/>

                <Link to="/signup">Create an account</Link>

            </form>
        </div>
    );
}

export default LoginPage;
