import "./Navbar.css";
import navbarLogo from "../.././assets/navbar-logo.png"
import {Link, useLocation, useNavigate} from "react-router-dom";
import {useEffect,useState} from 'react'
import {useAccountData, useAccountDataUpdate} from "../AccountContext/AccountProvider";

function Navbar() {
    const [links,setLinks] = useState([])
    const location = useLocation()

    const [isMobile,setIsMobile] = useState(window.innerWidth > 800 ? true : false);

    const AccountData = useAccountData()
    const AccountDateUpdate = useAccountDataUpdate()
    const navigate = useNavigate();

    const SignOut = () =>{
        AccountDateUpdate({
            isLoggedIn:false
        })

        navigate('/')
    }

    useEffect(()=>{
        window.addEventListener('resize',()=>{
            if(window.innerWidth > 800 && isMobile){
                console.log(`Going false. Width: ${window.innerWidth}, isMobile: ${isMobile}`)
                setIsMobile(false)
            }
            if(window.innerWidth <= 800 && !isMobile){
                console.log(`Going true. Width: ${window.innerWidth}, isMobile: ${isMobile}`)
                setIsMobile(true)
            }
        })
    },[])


    useEffect(()=>{
        let Links = []

        switch(location.pathname.split('/')[1]){
            case "":
                if(AccountData.isLoggedIn){
                    Links.push(<Link to="/account" key="account-link">Account</Link>)
                    Links.push(<button key="signout-link" onClick={SignOut}>Sign out</button>)
                }
                else{
                    Links.push(<Link to="/login" key="login-link">{isMobile ? "L" : "Login"}</Link>)
                    Links.push(<Link to="/signup" key="signup-link">Sign up</Link>)
                }
                Links.push(<Link to="/panel" key="panel-link">Panel</Link>)
                break;
            case "login":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "signup":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "404":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "book":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                break;
            case "account":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                Links.push(<button key="signout-link" onClick={SignOut}>Sign out</button>)
                break;
            case "panel":
                Links.push(<Link to="/" key="home-link">Home</Link>)
                Links.push(<Link to="/404" key="account-link">Account</Link>)
                Links.push(<button key="signout-link" onClick={SignOut}>Sign out</button>)
                break;
            default:
                Links.push(<Link to="/" key="home-link">Unknown location (see Navbar.js)</Link>)
                break;
        }
        setLinks(Links)
    },[useLocation(),AccountData,isMobile])

    return (
        <div className="navbar">
            <img src={navbarLogo} alt="remigoo logo"/>
            <div className="navbar-links">
                {links}
            </div>
        </div>
    );
}

export default Navbar;
